INSERT INTO `oc_information_description` (`information_id`, `language_id`, `title`, `description`) VALUES
(3, 1, 'Privacy Policy', '&lt;p&gt;We respect your privacy!&lt;/p&gt;\r\n\r\n&lt;p&gt;Any and all the information collected on this site will be kept strictly confidential and will not be sold, reused, rented, disclosed, or loaned!&lt;/p&gt;\r\n\r\n&lt;p&gt;Any information you provide will be held with the utmost care and will not be used in ways that you have not consented to.&lt;/p&gt;\r\n\r\n&lt;p&gt;If you have any questions, please feel free to call or e-mail us at info@Magazines-INDIA.com&lt;/p&gt;\r\n'),
(5, 1, 'Terms &amp; Conditions', '&lt;p&gt;\r\n	Terms &amp;amp; Conditions&lt;/p&gt;\r\n'),
(4, 1, 'About Us', '&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;&lt;strong&gt;Magazines-INDIA.com&lt;/strong&gt; offers magazines on Advertising &amp;amp; Media, Animals &amp;amp; Pets, Art &amp;amp; Architecture, Auto &amp;amp; Cycles, Aviation, Business &amp;amp; Finance, Career, Celebrity News, Children, Computers &amp;amp; IT, Cooking, Design &amp;amp; Graphic Arts, Education, Electronics, Engineering &amp;amp; Technology, Entertainment &amp;amp; TV, Environment, Fashion, Foreign Languages, General Interest, Health &amp;amp; Fitness, Healthcare &amp;amp; Nursing, History, Hobbies &amp;amp; Crafts, Home &amp;amp; Garden, Hotels &amp;amp; Hospitality, Journals, Lifestyles &amp;amp; Cultures, Literature, Men, Music &amp;amp; Dance, News &amp;amp; Politics, Parenting &amp;amp; Families, Petroleum, Oil &amp;amp; Gas, Photography &amp;amp; Video, Puzzles &amp;amp; Games, Real Estate, Regional Magazines, Religious &amp;amp; Spiritual, Science &amp;amp; Nature, Special Needs, Sports &amp;amp; Recreation, Style &amp;amp; Beauty, Teen, Telecommunications, Trade Magazines, Travel &amp;amp; Tourism, Women.&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;&lt;strong&gt;Besides above&lt;/strong&gt;, you can find a &lt;strong&gt;wide range of Academic Journals&lt;/strong&gt; – National &amp;amp; International on Arts &amp;amp; Literature, Biological Sciences, Business Administration, Chemistry, Computer Science, Earth Sciences, Economics, Education, Engineering, Environmental Sciences, Humanities, Law, Linguistics, Materials Science, Mathematics, Medicine, Philosophy, Physics, Psychology, Social Sciences, Sports &amp;amp; Recreation.&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;110&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture13.jpg&quot; width=&quot;105&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We strive to get you the choice of your magazine with our existing 10000 plus and ever increasing titles. We understand that today’s readers are more inquisitive and want to explore newer avenues. Keeping this in mind we always keep updating our inventory frequently.&amp;nbsp; Choose magazines from your favorite subject.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;96&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture14.jpg&quot; width=&quot;92&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We are one of the most comprehensive sources for supplying Magazines from all around the world online, at an affordable &amp;amp; economical price. We are here to serve your reading appetite just at your doorstep.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;97&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture15.jpg&quot; width=&quot;92&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We process your orders immediately, with the publishers and make sure that you get your subscription started quickly, and receive issues on time. We invite you to experience an efficient service that delivers right at your door step.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;89&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture16.jpg&quot; width=&quot;85&quot; /&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We provide 24 hours service to our customers, connect with us any time, any day, we are always at your service. Your satisfaction is our ultimate goal and we''re dedicated to deliver it!&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture17.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;If you need any assistance, with your selection or order, you can write to customer support. We provide unique experience for users and offer the widest variety of magazines from all corners of world to cater to every kind of reader.&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture18.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;Get gifts on your subscriptions, as offered by the publishers on various magazines. Check on the offers available with magazines subscriptions, in gift section.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture19.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We offer free home delivery; all the issues will be sent free of delivery charges to your address.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture20.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We are here to serve your reading appetite, just at your doorstep. With a huge assortment of magazine genres, it’s a world of magazines from all around the globe in one place. Just browse, choose and subscribe your magazine and we will deliver it to you promptly.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture21.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;You can save money, by using our services, on magazines subscriptions and get great discounts.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture22.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We provide unique experience for users and offer the widest variety of magazines from all corners of world to cater to every kind of reader. We strive to get you the choice of your magazine with our existing 100000 plus and ever increasing titles. We understand that today’s readers are more inquisitive and want to explore newer avenues. Keeping this in mind we always keep updating our inventory frequently.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture23.jpg&quot; width=&quot;95&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;If you wish to write to us, you can email us at &lt;a href=&quot;mailto:subscribe@Magazines-INDIA.com&quot;&gt;subscribe@Magazines-INDIA.com&lt;/a&gt;, we will be happy to assist you!&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture24.jpg&quot; width=&quot;95&quot; /&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;We make, managing and renewing subscriptions, easy, whether it''s 1 or 100 magazine subscriptions, we''ll save you time and money. Shop with a click of button! You will find everything at One Place - Review, Subscribe, Renew...its Easy!&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture25.jpg&quot; width=&quot;123&quot; /&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot; style=&quot;font-size:14px;&quot;&gt;Sign up for free updates and newsletters on promotional offers.&lt;/p&gt;\r\n\r\n&lt;p align=&quot;center&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;99&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/aboutus/Picture26.jpg&quot; width=&quot;177&quot; /&gt;&amp;nbsp;&lt;/p&gt;\r\n'),
(6, 1, 'Delivery Information', '&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture6.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;We offer free shipping on all the magazines!&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;What are the delivery charges?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;All prices listed, are inclusive of delivery costs. There will be no extra delivery charges. The deliveries are handled by each of our publishing partners. The publishers have been handling the delivery of magazine subscriptions for many years. They have developed a great system that ensures the titles arrive in your letterbox on time every time. If you are concerned about a delivery please contact us at subscribe@Magazines-INDIA.com and we will be delighted to look after your request.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How long will it take to receive my first issue?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Usually, your first magazine will start arriving within Four to Six weeks after we receive your order, but it does depend on the frequency of the magazine you’ve chosen and to which city, it is being shipped to.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Sometimes, there can be postal problems or other difficulties that will affect your service, but we pledge to stand by and guarantee full delivery of every last issue. If you have any problems just let us know, and we’ll get right to work and fix the problem, then we’ll either replace the specific copies you missed, or credit your service with extra issues to make up for it.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Once we receive your order, it will take a week or two for your subscription to be received and scheduled by the publisher, where it will then be included with the publisher’s next production cycle and shipped via standard mail. For example, if a subscription to a monthly magazine is ordered and received by the publisher in June, and the July or even August issue has already been labeled and/or mailed, your first issue would be included with the August or September mailing.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Please be assured that these are standard industry lead times, although it can vary from publisher to publisher. The actual delivery date of your first issue will depend on when the next issue is scheduled to be distributed by the publisher and how frequently the magazine you’ve ordered is published, although our system does allow the fastest processing possible.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;If you have any questions about delivery, please call our Customer Service or send an email to subscribe@Magazines-INDIA.com&lt;/li&gt;\r\n&lt;/ul&gt;\r\n'),
(7, 1, 'FAQ - Frequently Asked Questions', '&lt;ul&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;What are the main products that can be delivered to my home or office?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;On what subjects, I can find magazines?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How do I place an order?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How can I make payment for my order?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How can I request a refund?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;What are the delivery charges?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How long will it take to receive my first issue?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How do I change my mailing address?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How do I find a specific magazine?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How many magazines do you have?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;If I do not find a magazine I am looking for, will you help us in procuring it?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How a Library can place their order?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How can I get my magazine listed on the site?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;How can we give advertisement in the magazines, which are listed on your website?&lt;/strong&gt;&lt;/li&gt;\r\n	&lt;li style=&quot;list-style: inside none disc;&quot;&gt;&lt;strong&gt;Are there any vacancies?&lt;/strong&gt;&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;list-style: inside none disc;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;PRODUCTS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/picture1.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;What are the main products that can be delivered to my home or office?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;Magazines – National, Regional &amp;amp; International&lt;/li&gt;\r\n	&lt;li&gt;Trade Publications&lt;/li&gt;\r\n	&lt;li&gt;Newspapers – National &amp;amp; International&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;SUBJECTS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;&lt;img height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/picture2.jpg&quot; width=&quot;150&quot; /&gt;&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;On what subjects, I can find magazines?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;You can find magazines on Advertising &amp;amp; Media, Animals &amp;amp; Pets, Antiques &amp;amp; Collectibles, Architecture &amp;amp; Design, Art &amp;amp; Photography, Arts &amp;amp; Cultures, Auto &amp;amp; Cycles, Business, Finance &amp;amp; Money, Career &amp;amp; Industry, Carpets &amp;amp; Handicrafts, Celebrity News &amp;amp; Gossip, Children, Computers &amp;amp; Information Technology, Construction &amp;amp; Contracting, Cooking, Food &amp;amp; Beverage, Crafts, Current Events &amp;amp; News, Dentistry, Education, Engineering, Enrichment, Entertainment &amp;amp; TV, Environmental, Ethnic, Fashion, Style &amp;amp; Beauty, Gaming, General Interest, Graphic Arts, Health &amp;amp; Fitness, History, Hobbies, Crafts &amp;amp; Collectibles, Home &amp;amp; Gardening, Hospitality Industry, Internet, Large Print Titles, Lifestyles &amp;amp; Cultures, Machine &amp;amp; Construction, Manufacturing, Mechanical, Medical, Men’s Interest, Motoring &amp;amp; Transport, Multimedia Design, Music &amp;amp; Literature, Network Communication, News &amp;amp; Politics, Nursing, Outdoors, Parenting &amp;amp; Families, Pharmacy &amp;amp; Pharmaceuticals, Photography &amp;amp; Video, Plastic &amp;amp; Polymers, Politics &amp;amp; Commentary, Publishing, Puzzles &amp;amp; Games, Religious &amp;amp; Spiritual, Retail Distribution, Sales &amp;amp; Marketing, Science &amp;amp; Nature, Sports &amp;amp; Recreation, Teen, Telecommunications, Textile Industry, Travel &amp;amp; Tourism, Video Games, Women’s Interest.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;ORDERS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture3.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How do I place an order?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;Simply either by viewing, selecting and purchasing online, or by emailing us the list of publications you need, to subscribe@Magazines-INDIA.com&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture4.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Are your prices economical?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;When you order subscriptions from us, we guarantee you’ll almost always realize significant savings over subscription prices because our rates are affordable and economical. We can supply magazines from hundreds of International publishers, and are constantly working to get the lowest possible rates: that’s why you’ll save maximum on your favorite magazines subscriptions with us.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture5.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How can I make payment for my order?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;You can pay online using your credit card, through payment gateway, or can pay by Bank Draft / Cheque or Bank Transfer. Please get in touch with Customer Support for more details at support@Magazines-INDIA.com&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How can I request a refund?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;All orders are fully cancelable as long as the cancel request is received within 24 hours of the time the order is placed. After that, in most cases cancellations will not be accepted. There’s a good reason for this policy.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;All orders are pre-paid to the publishers and processed promptly so as to ensure that the subscription begins as quickly as possible. We negotiate with publishers for the lowest rates available by pledging that we will only process legitimate, pre-paid orders from consumers who are sure of what they want when placing orders. This saves time and money for all parties and seems to work well.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;We do recognize that special situations may arise when a magazine ceases publication or editorial content changes. In such events, please contact us at &lt;a href=&quot;mailto:info@Magazines-INDIA.com&quot;&gt;info@Magazines-INDIA.com&lt;/a&gt;, all such cases will be handled on an individual, case-by-case basis.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How can I cancel my order?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;Please contact us by email customer service at info@Magazines-INDIA.com to cancel or replace your existing subscription.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;DELIVERY&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture6.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;We offer free shipping on all the magazines!&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;What are the delivery charges?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;All prices listed, are inclusive of delivery costs. There will be no extra delivery charges. The deliveries are handled by each of our publishing partners. The publishers have been handling the delivery of magazine subscriptions for many years. They have developed a great system that ensures the titles arrive in your letterbox on time every time. If you are concerned about a delivery please contact us at subscribe@Magazines-INDIA.com and we will be delighted to look after your request.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How long will it take to receive my first issue?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;Usually, your first magazine will start arriving within Four to Six weeks after we receive your order, but it does depend on the frequency of the magazine you’ve chosen and to which city, it is being shipped to.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;Sometimes, there can be postal problems or other difficulties that will affect your service, but we pledge to stand by and guarantee full delivery of every last issue. If you have any problems just let us know, and we’ll get right to work and fix the problem, then we’ll either replace the specific copies you missed, or credit your service with extra issues to make up for it.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;Once we receive your order, it will take a week or two for your subscription to be received and scheduled by the publisher, where it will then be included with the publisher’s next production cycle and shipped via standard mail. For example, if a subscription to a monthly magazine is ordered and received by the publisher in June, and the July or even August issue has already been labeled and/or mailed, your first issue would be included with the August or September mailing.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;Please be assured that these are standard industry lead times, although it can vary from publisher to publisher. The actual delivery date of your first issue will depend on when the next issue is scheduled to be distributed by the publisher and how frequently the magazine you’ve ordered is published, although our system does allow the fastest processing possible.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;If you have any questions about delivery, please call our Customer Service or send an email to subscribe@Magazines-INDIA.com&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How do i change my mailing address?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;At any time during your subscription, you may change your address by contacting us by email or phone.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;OTHERS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture7.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How do I find a specific magazine?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;A search box is available at the top of every page throughout the site. You may also browse for a magazine title by category. Category listings are found on the left-hand side of each page.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p style=&quot;text-align:justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How many magazines do you have?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;We offer subscriptions for more than 100,000 plus magazines, trade publications, professional Journals from all over the world.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;If I do not find a magazine I am looking for, will you help us in procuring it?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li align=&quot;justify&quot;&gt;If there is a magazine you’d like to subscribe that you don’t find here, Write to us at info@Magazines-INDIA.com and check back in a week’s time. Upon receipt of your request, we’ll try to offer the title you’re looking for as soon as possible.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;INSTITUTIONAL &amp;amp; LIBRARY ORDERS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture8.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;strong&gt;How a Library can place their order?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;Our Library Express Order Line offers a unique subscription service that can be tailored to meet the specific needs of libraries, schools &amp;amp; public institutions. You will get a dedicated account manager, who will assist you with your requirements thorough the subscription period.&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;CORPORATE ORDERS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;justify&quot;&gt;&lt;a href=&quot;http://subscribetomagazinesonline.in/wp-content/uploads/2012/07/Corporates-Orders1.jpg&quot;&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture9.jpg&quot; width=&quot;150&quot; /&gt;&lt;/a&gt;&lt;br /&gt;\r\nYes, we process Corporate Orders, too!&amp;nbsp; Our dedicated corporate order service provides customers, with an ability to buy various titles from different publishers through one source, rather than visiting individual publisher sites, saving them money, efforts and time. If you are a corporate house, please feel free to write to support@Magazines-INDIA.com, and send your selection of Magazine, upon receipt, our customer service representative will get in touch with you, with further details.&lt;/p&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;PROMOTIONS&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture10.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How can i get my magazine listed on the site?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;If you’re a magazine publisher anywhere in the world, and want a new source of quality, paid circulation, we invite you to join us.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;If your magazine is not listed at our store, you’re missing out on a very cost effective and growing source of sale, no matter what your audience is.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;We welcome your inquiries and participation, so drop us a line at info@Magazines-INDIA.com and we’ll get back to you to increase your advertising and circulation numbers.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;ADVERTISING&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture11.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;How can we give advertisement in the magazines, which are listed on your website?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;We have a great way for advertisers to connect with customers, increase sales and build your consumer franchise.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;Advertise in Our Featured Publications – If you’re interested in exploring print advertising opportunities in any of the magazines listed on our site, we can help you get in touch with the advertising sales professional at your preferred publication.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;To get started with a magazine that can provide the perfect target for your advertising message and can offer rich promotional opportunities for your company, send an email at advertise@Magazines-INDIA.com to request a media kit, advertising information or more details.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;p&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;EMPLOYMENT OPPORTUNITIES&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;img alt=&quot;&quot; border=&quot;0&quot; height=&quot;150&quot; src=&quot;http://linkdemo.in/magazinesindia/image/information/faq/Picture12.jpg&quot; width=&quot;150&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p&gt;&lt;strong&gt;Are there any vacancies?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li&gt;We are always on the lookout for fresh, energetic talent to join our growing team.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n\r\n&lt;ul&gt;\r\n	&lt;li style=&quot;text-align:justify&quot;&gt;We offer competitive salaries and benefits, as well as an easygoing work environment. Most importantly, we offer the challenge of working with an entrepreneurial team that encourages independence and project ownership while helping build a unique company. You can write to us at info@Magazines-INDIA.com to know more about Vacancies.&lt;/li&gt;\r\n&lt;/ul&gt;\r\n');
